package Javawork;
import java.util.*;
public class Print1toN {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in = new Scanner(System.in);
		System.out.println("Enter N: ");
		int n = in.nextInt();
		for(int i=0; i<=n;i++) {
			System.out.println(i);
		}

	}

}
